CREATE TABLE `chat_content` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '自增Id',
  `sendId` bigint NOT NULL COMMENT '发送人Id',
  `receiverId` bigint NOT NULL COMMENT '接收人Id',
  `content` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '聊天内容',
  `type` int NOT NULL COMMENT '内容类型：0html文本，1图片',
  `state` int NOT NULL COMMENT '是否发送成功：0否，1是',
  `noCode` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '时间戳',
  `createDateUtc` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '创建时间',
  `title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '拓展功能字段',
  `description` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '拓展功能字段',
  `label` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '拓展功能字段',
  `thumbnail` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '拓展功能字段',
  `readFlag` tinyint(1) NOT NULL COMMENT '是否阅读：0否，1是',
  `avatar` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '头像',
  `soundStatus` int DEFAULT '0' COMMENT '音频播放状态：0未播放，1播放中，2已播放',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=139 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci ROW_FORMAT=DYNAMIC;

